mapper.select:
SELECT id, cart_id, product_id, quantity, created_at, updated_at
FROM cart_items
WHERE cart_id = #{cartId}
  AND product_id = #{productId}

mapper.update:
UPDATE cart_items
SET quantity = #{quantity},
    updated_at = CURRENT_TIMESTAMP
WHERE cart_id = #{cartId}
  AND product_id = #{productId}

mapper.delete:
DELETE FROM cart_items
WHERE cart_id = #{cartId}
  AND product_id = #{productId}
